package utils

import (
	"encoding/base64"
	"fmt"
	"runtime"
)

var bannerBase64 = ""

var versionTpl = `
%s

Name: git-toolkit
Version: %s
Author: Tony Deng <wolf.deng@gmail.com>
Arch: %s
BuildTime: %s
CommitID: %s
`

// 构建版本信息
func GenVersion(version string, buildTime string, commitId string) string {
	banner, _ := base64.StdEncoding.DecodeString(bannerBase64)
	return fmt.Sprintf(versionTpl, banner, version, runtime.GOOS+"/"+runtime.GOARCH, buildTime, commitId)
}
